SELECT roomify_properties_field_data_field_st_property_reference.property_id AS roomify_properties_field_data_field_st_property_reference_pr
FROM {bat_bookings} bat_bookings
LEFT JOIN {field_data_booking_event_reference} field_data_booking_event_reference ON bat_bookings.booking_id = field_data_booking_event_reference.entity_id AND (field_data_booking_event_reference.entity_type = 'bat_booking' AND field_data_booking_event_reference.deleted = '0')
LEFT JOIN {bat_events} bat_events_field_data_booking_event_reference ON field_data_booking_event_reference.booking_event_reference_target_id = bat_events_field_data_booking_event_reference.event_id
LEFT JOIN {field_data_booking_user_guest} field_data_booking_user_guest ON bat_bookings.booking_id = field_data_booking_user_guest.entity_id AND (field_data_booking_user_guest.entity_type = 'bat_booking' AND field_data_booking_user_guest.deleted = '0')
INNER JOIN {users} users_field_data_booking_user_guest ON field_data_booking_user_guest.booking_user_guest_target_id = users_field_data_booking_user_guest.uid
LEFT JOIN {field_data_event_bat_unit_reference} bat_events_field_data_booking_event_reference__field_data_event_bat_unit_reference ON bat_events_field_data_booking_event_reference.event_id = bat_events_field_data_booking_event_reference__field_data_event_bat_unit_reference.entity_id AND (bat_events_field_data_booking_event_reference__field_data_event_bat_unit_reference.entity_type = 'bat_event' AND bat_events_field_data_booking_event_reference__field_data_event_bat_unit_reference.deleted = '0')
LEFT JOIN {bat_units} bat_units_field_data_event_bat_unit_reference ON bat_events_field_data_booking_event_reference__field_data_event_bat_unit_reference.event_bat_unit_reference_target_id = bat_units_field_data_event_bat_unit_reference.unit_id
LEFT JOIN {bat_types} bat_types_bat_units ON bat_units_field_data_event_bat_unit_reference.type_id = bat_types_bat_units.type_id
LEFT JOIN {field_data_field_st_property_reference} bat_types_bat_units__field_data_field_st_property_reference ON bat_types_bat_units.type_id = bat_types_bat_units__field_data_field_st_property_reference.entity_id AND (bat_types_bat_units__field_data_field_st_property_reference.entity_type = 'bat_type' AND bat_types_bat_units__field_data_field_st_property_reference.deleted = '0')
LEFT JOIN {roomify_properties} roomify_properties_field_data_field_st_property_reference ON bat_types_bat_units__field_data_field_st_property_reference.field_st_property_reference_target_id = roomify_properties_field_data_field_st_property_reference.property_id
WHERE (( (bat_bookings.booking_id = :booking_id ) ))
LIMIT 1
